import { useInput } from "lx-react-form";

export const name = useInput({
  name: "name",
});

export const email = useInput({
  name: "email",
  validation: "email",
});

export const password = useInput({
  name: "password",
  validation: "senha",
});

export const confirmPassword = useInput({
  name: "confirmPassword",
  same: password.value,
});
