import React, { useContext } from "react";
import { useForm } from "lx-react-form";

import { UserContext } from "../../contexts/UserContext";
import { iUserRegister } from "./typeRegister";
import { Link } from "react-router-dom";

 const RegisterForm = () => {
  const { register } = useContext(UserContext);

   const name = useInput({
    name: "name",
  });

   const email = useInput({
    name: "email",
    validation: "email",
  });

   const password = useInput({
    name: "password",
    validation: "senha",
  });

   const confirmPassword = useInput({
    name: "confirmPassword",
    same: password.value,
  });

  const form = useForm<iUserRegister>({
    clearFields: true,
    formFields: [name, email, password, confirmPassword],
    submitCallback: (formData) => {
      console.log(formData);
    },
  });

  return (
    <form onSubmit={form.register}>
        <div>
            <label htmlFor="name">Nome</label>
            <input
            id="name" 
            type="text"
            placeholder="Digite o seu nome" 
            {...name.inputProps}
            />
        </div>
       {name.error && <span>{name.error}</span>}

       <button type="submit">Cadastrar</button>

       <p>Já possui uma conta? <Link to={"/"}>Faça Login</Link></p>
    </form>
  );
};
