import { useInput } from "lx-react-form";
import React from "react";

  export const name = useInput({
    name: "name",
  });

  const email = useInput({
    name: "email",
    validation: "email",
  });

  const password = useInput({
    name: "password",
    validation: "senha",
  });

  const confirmPassword = useInput({
    name: "confirmPassword",
    same: password.value,
  });
