import React, { createContext, useState } from "react";
import { useNavigate } from "react-router-dom";

import { ApiRequests } from "../services/ApiRequests";
import { iChildren, iUser } from "./typeContext";
import { iUserRegister } from "../Pages/Register/typeRegister";

export const UserContext = createContext({});

export const UserProvider = ({ children }: iChildren) => {
  const [user, setUser] = useState<iUser | null>(null);

  const navigate = useNavigate();

  const register = async (data: iUserRegister) => {
    try {
      await ApiRequests.post("/register", data);
      navigate("/");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <UserContext.Provider value={{ user, setUser, register }}>
      {children}
    </UserContext.Provider>
  );
};
